// Process AssemblyAI results
const transcript = $input.first().json;

if (!transcript) {
  throw new Error('No transcript data received');
}

console.log('üé§ AssemblyAI analysis complete');
console.log(`üìä Status: ${transcript.status}`);
console.log(`‚è±Ô∏è Duration: ${transcript.audio_duration}s`);

// Extract insights
const insights = {
  speakers: transcript.speaker_labels?.speakers || [],
  sentiment: transcript.sentiment_analysis?.results || [],
  highlights: transcript.auto_highlights?.results || []
};

// Analyze conversation patterns
const conversationAnalysis = {
  totalSpeakers: insights.speakers.length,
  sentimentBreakdown: {
    positive: insights.sentiment.filter(s => s.sentiment === 'POSITIVE').length,
    negative: insights.sentiment.filter(s => s.sentiment === 'NEGATIVE').length,
    neutral: insights.sentiment.filter(s => s.sentiment === 'NEUTRAL').length
  },
  keyTopics: insights.highlights.map(h => h.text).slice(0, 5)
};

console.log('üé≠ Conversation Analysis:');
console.log(`üë• Speakers: ${conversationAnalysis.totalSpeakers}`);
console.log(`üé≠ Sentiment: ${JSON.stringify(conversationAnalysis.sentimentBreakdown)}`);
console.log(`üìù Key Topics: ${conversationAnalysis.keyTopics.join(', ')}`);

return {
  json: {
    success: true,
    transcript,
    insights,
    conversationAnalysis,
    message: 'üéâ AssemblyAI analysis complete!',
    timestamp: new Date().toISOString()
  }
};
